import React, { useState, useEffect } from 'react';
import DonationForm from './DonationForm';
import './DonorDashboard.css';

function DonorDashboard() {
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [donations, setDonations] = useState([]); // State to hold real data

  // This useEffect hook will run once when the component loads
  useEffect(() => {
    const fetchDonations = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/donations');
        const data = await response.json();
        setDonations(data); // Save the fetched data in state
      } catch (error) {
        console.error("Failed to fetch donations:", error);
      }
    };

    fetchDonations();
  }, []); // The empty array ensures this runs only once on mount

  const handleCreateDonation = async (formData) => {
    try {
      const response = await fetch('http://localhost:5000/api/donations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error('Network response was not ok');

      const newDonation = await response.json();
      
      // Add the new donation to the top of our list in the UI
      setDonations(prevDonations => [newDonation, ...prevDonations]);
      
      alert('Donation created successfully!'); // Success alert!

    } catch (error) {
      console.error('Error creating donation:', error);
      alert('Failed to create donation. Please try again.');
    }

    setIsFormVisible(false);
  };

  // We now use the 'donations' state instead of dummy data
  const activeDonations = donations.filter(d => d.status !== 'Delivered');
  const pastDonations = donations.filter(d => d.status === 'Delivered');

  return (
    <div className="dashboard-container">
      {isFormVisible && (
        <DonationForm 
          onClose={() => setIsFormVisible(false)}
          onSubmit={handleCreateDonation}
        />
      )}

      <header className="dashboard-header">
        <h1>ZeroBite Bhimavaram</h1>
        <h2>Donor Dashboard</h2>
      </header>

      <main className="dashboard-main">
        <div className="dashboard-actions">
          <button 
            className="create-donation-btn"
            onClick={() => setIsFormVisible(true)}
          >
            + Create New Donation
          </button>
        </div>

        <section className="donations-section">
          <h3>Active Donations</h3>
          <div className="donations-list">
            {activeDonations.length > 0 ? (
              activeDonations.map(donation => (
                <div key={donation._id} className="donation-card">
                  <p className="donation-food">{`${donation.foodDescription} (${donation.quantity} portions)`}</p>
                  <p className="donation-status pending">{donation.status}</p>
                </div>
              ))
            ) : (
              <p>No active donations.</p>
            )}
          </div>
        </section>

        <section className="donations-section">
          <h3>Donation History</h3>
          <div className="donations-list">
            {pastDonations.map(donation => (
              <div key={donation._id} className="donation-card">
                <p className="donation-food">{`${donation.foodDescription} (${donation.quantity} portions)`}</p>
                <p className="donation-status delivered">{donation.status}</p>
              </div>
            ))}
          </div>
        </section>
      </main>
    </div>
  );
}

export default DonorDashboard;
