import React, { useState } from 'react';
import './AuthPage.css';

function AuthPage({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [role, setRole] = useState('Donor');
  const [address, setAddress] = useState('');
  const [capacity, setCapacity] = useState('');
  const [employeeId, setEmployeeId] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = isLogin ? '/api/users/login' : '/api/users/register';
    
    let payload = { email, password };
    if (!isLogin) {
      payload = { name, email, password, role, address, capacity, employeeId };
    }

    try {
      const response = await fetch(`http://localhost:5000${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.msg || 'Something went wrong');
      }

      if (isLogin) {
        onLogin(data.token); // This will now also save the token in App.js
      } else {
        alert('Registration successful! Please log in.');
        setIsLogin(true);
      }
    } catch (error) {
      alert(`Error: ${error.message}`);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-form">
        <form onSubmit={handleSubmit}>
          <h2>{isLogin ? 'Login' : 'Register'}</h2>
          {!isLogin && (
            <div className="form-group">
              <label>Name</label>
              <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
            </div>
          )}
          <div className="form-group">
            <label>Email</label>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          </div>
          {!isLogin && (
            <>
              <div className="form-group">
                <label>I am a...</label>
                <select value={role} onChange={(e) => setRole(e.target.value)}>
                  <option value="Donor">Donor (Restaurant, Hall)</option>
                  <option value="Charity">Charity (Shelter)</option>
                  <option value="Worker">Municipality Worker</option>
                </select>
              </div>

              {role === 'Donor' && (
                <div className="form-group">
                  <label>Full Address</label>
                  <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} placeholder="Your restaurant's full address" required />
                </div>
              )}
              {role === 'Charity' && (
                <div className="form-group">
                  <label>Capacity (Number of people you serve)</label>
                  <input type="number" value={capacity} onChange={(e) => setCapacity(e.target.value)} placeholder="e.g., 100" required />
                </div>
              )}
              {role === 'Worker' && (
                <div className="form-group">
                  <label>Municipality Employee ID</label>
                  <input type="text" value={employeeId} onChange={(e) => setEmployeeId(e.target.value)} placeholder="Your official ID" required />
                </div>
              )}
            </>
          )}
          <button type="submit" className="auth-btn">{isLogin ? 'Login' : 'Register'}</button>
          <p className="toggle-auth" onClick={() => setIsLogin(!isLogin)}>
            {isLogin ? "Don't have an account? Register" : 'Already have an account? Login'}
          </p>
        </form>
      </div>
    </div>
  );
}

export default AuthPage;
