import React, { useState } from 'react';
import DonationForm from './DonationForm';
import './DonorDashboard.css';

const dummyDonations = [
  { id: 1, food: 'Vegetable Biryani (10 portions)', status: 'Pending Pickup' },
  { id: 2, food: 'Idli & Sambar (25 portions)', status: 'Delivered' },
  { id: 3, food: 'Chapathi with Paneer Curry (15 portions)', status: 'Delivered' },
];

function DonorDashboard() {
  const [isFormVisible, setIsFormVisible] = useState(false);

  const activeDonations = dummyDonations.filter(d => d.status !== 'Delivered');
  const pastDonations = dummyDonations.filter(d => d.status === 'Delivered');

  const handleCreateDonation = async (formData) => {
    console.log("Sending data to backend:", formData);
    
    try {
      const response = await fetch('http://localhost:5000/api/donations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const newDonation = await response.json();
      console.log('Successfully created new donation:', newDonation);
      // Later, we will update the list of donations on the screen
      
    } catch (error) {
      console.error('Error creating donation:', error);
      alert('Failed to create donation. Please try again.');
    }

    setIsFormVisible(false); // Hide the form after submission
  };

  return (
    <div className="dashboard-container">
      {isFormVisible && (
        <DonationForm 
          onClose={() => setIsFormVisible(false)}
          onSubmit={handleCreateDonation}
        />
      )}

      <header className="dashboard-header">
        <h1>ZeroBite Bhimavaram</h1>
        <h2>Donor Dashboard</h2>
      </header>

      <main className="dashboard-main">
        <div className="dashboard-actions">
          <button 
            className="create-donation-btn"
            onClick={() => setIsFormVisible(true)}
          >
            + Create New Donation
          </button>
        </div>

        <section className="donations-section">
          <h3>Active Donations</h3>
          <div className="donations-list">
            {activeDonations.length > 0 ? (
              activeDonations.map(donation => (
                <div key={donation.id} className="donation-card">
                  <p className="donation-food">{donation.food}</p>
                  <p className="donation-status pending">{donation.status}</p>
                </div>
              ))
            ) : (
              <p>No active donations.</p>
            )}
          </div>
        </section>

        <section className="donations-section">
          <h3>Donation History</h3>
          <div className="donations-list">
            {pastDonations.map(donation => (
              <div key={donation.id} className="donation-card">
                <p className="donation-food">{donation.food}</p>
                <p className="donation-status delivered">{donation.status}</p>
              </div>
            ))}
          </div>
        </section>
      </main>
    </div>
  );
}

export default DonorDashboard;
